INSERT INTO `sanpham` (`id_sp`, `id_dm`, `ten_sp`, `anh_sp`, `tac_gia`, `nxb`, `so_trang`, `tinh_trang`, `trang_thai`, `dac_biet`, `chi_tiet_sp`) VALUES
(85, 1, 'DON QUIXOTE - NHÀ QUÝ TỘC TÀI BA XỨ MANCHA', '13693-don-quixote-nha-quy-toc-tai-ba-xu-mancha-1.jpg', 'Miguel De Cervantes', 'Kim Đồng', '1879', 'Mới 100%, Còn hàng', '', '0', '<p>Từ khi ra đời, cuốn tiểu thuyết Đ&ocirc;n Kih&ocirc;t&ecirc; - Nh&agrave; qu&yacute; tộc t&agrave;i ba xứ Mantra đ&atilde; chinh phục dư luận người đọc trong nước cũng như ngo&agrave;i nước. Ngay trong năm 1605, tại T&acirc;y Ban Nha, tập truyện đ&atilde; được t&aacute;i bản 5 lần, v&agrave; sinh thời, Xervant&ecirc;x đ&atilde; nh&igrave;n thấy t&aacute;c phẩm của m&igrave;nh được xuất bản 13 lần (6 lần ở T&acirc;y Ban Nha, 3 lần ở Bồ Đ&agrave;o Nha, 3 lần ở Bỉ, 1 lần ở &Yacute;). Trải qua gần 400 năm, vượt ra khỏi sự đ&agrave;o thải của thời gian, Đ&ocirc;n Kyh&ocirc;t&ecirc; - Nh&agrave; qu&yacute; tộc t&agrave;i ba xứ Mantra vẫn gi&agrave;nh được sự h&acirc;m mộ rộng khắp v&agrave; được c&ocirc;ng nhận l&agrave; một trong những t&aacute;c phẩm văn học lớn nhất của nh&acirc;n loại. Năm 1795, nh&agrave; đại văn h&agrave;o Đức, Gớt (Goethe), viết cho nh&agrave; thơ lớn Silơ (Schiller): &quot;T&ocirc;i đ&atilde; t&igrave;m thấy trong cuốn tiểu thuyết của Xervant&ecirc;x cả một kho t&agrave;ng th&uacute; vị v&agrave; bổ &iacute;ch&quot;.</p>\r\n'),
(86, 1, ' DUNE - XỨ CÁT', '13865-dune-xu-cat-1.jpg', 'Frank Herbert', 'Văn Học', '2752', 'Mới 100%, Còn hàng', '', '1', '<p>Một thời điểm rất xa trong tương lai&hellip;</p>\r\n\r\n<p>Từ đời n&agrave;y sang đời kh&aacute;c, người Fremen tr&ecirc;n h&agrave;nh tinh sa mạc lưu truyền lời ti&ecirc;n tri về một đấng cứu tinh sẽ dẫn dắt họ gi&agrave;nh lấy tự do đ&iacute;ch thực&hellip;</p>\r\n\r\n<p>Từ thế hệ n&agrave;y sang thế hệ kh&aacute;c, những nữ ph&ugrave; thủy Bene Gesserit mỏi m&ograve;n chờ đợi sự xuất hiện của một B.G. nam giới duy nhất, người c&oacute; thể vượt qua mọi giới hạn kh&ocirc;ng gian - thời gian&hellip;</p>\r\n\r\n<p>L&agrave; Lisal al-Gaib của người Fremen, l&agrave; Kwisatz Haderach của học viện Bene Gesserit, ch&agrave;ng c&ocirc;ng tước trẻ tuổi Paul Atreides đ&atilde; l&agrave;m tất cả những g&igrave; c&oacute; thể để thay đổi định mệnh đ&oacute;. Cha bị giết chết, mẹ bị cho l&agrave; kẻ phản bội, gia tộc bị t&agrave;n s&aacute;t, bản th&acirc;n bị săn đuổi đến đường c&ugrave;ng, Paul đơn độc dấn th&acirc;n v&agrave;o một cuộc phi&ecirc;u lưu sinh tử, kh&ocirc;ng hề biết rằng mỗi h&agrave;nh động của m&igrave;nh sẽ g&oacute;p phần quyết định vận mệnh của cả thi&ecirc;n h&agrave;. Sa mạc Arrakis khắc nghiệt tưởng như sẽ l&agrave; nơi ch&ocirc;n v&ugrave;i vĩnh viễn vinh quang của gia tộc Atreides, nhưng h&oacute;a ra lại th&agrave;nh điểm khởi đầu cho một huyền thoại mới&hellip;</p>\r\n\r\n<p>L&agrave; một trong những cuốn tiểu thuyết khoa học giả tưởng b&aacute;n chạy nhất mọi thời đại, Xứ C&aacute;t kh&ocirc;ng chỉ l&agrave; lựa chọn đối với những t&iacute;n đồ của Ch&uacute;a nhẫn, Chiến tranh giữa c&aacute;c v&igrave; sao&hellip; m&agrave; c&ograve;n chinh phục độc giả đủ mọi lứa tuổi, mọi tầng lớp v&agrave; sở th&iacute;ch bởi sự đa dạng v&agrave; phức tạp của con người v&agrave; kh&ocirc;ng gian trong truyện, bởi sự tinh tế trong x&acirc;y dựng t&acirc;m l&yacute;, bởi sự hấp dẫn, căng thẳng v&agrave; bất ngờ của cốt truyện, bởi sự độc đ&aacute;o v&agrave; th&uacute; vị của khối lượng kiến thức khổng lồ cũng như bởi sự hấp dẫn trong những tư tưởng về t&ocirc;n gi&aacute;o, về tự do, về t&igrave;nh y&ecirc;u, về sự sống v&agrave; c&aacute;i chết&hellip;</p>\r\n\r\n<p>Nhận định</p>\r\n\r\n<p>Độc nhất v&ocirc; nhị&hellip; Kh&ocirc;ng g&igrave; c&oacute; thể s&aacute;nh nổi, ngoại trừ Ch&uacute;a nhẫn. Arthur C. Clarke</p>\r\n\r\n<p>C&oacute; lẽ kh&oacute; khăn lớn nhất khi đọc quyển s&aacute;ch n&agrave;y ch&iacute;nh l&agrave; phải đặt n&oacute; xuống. Classic Sci-Fi</p>\r\n\r\n<p>&ldquo;Bức tranh to&agrave;n cảnh về một x&atilde; hội ngo&agrave;i h&agrave;nh tinh ho&agrave;n thiện v&agrave; chi tiết hơn bất kỳ t&aacute;c giả n&agrave;o trong lĩnh vực n&agrave;y từng dựng được&hellip; Một c&acirc;u chuyện l&ocirc;i cuốn vừa bởi h&agrave;nh động vừa bởi tầm nh&igrave;n triết học.&rdquo; Washington Post Book World</p>\r\n'),
(87, 1, 'ÔNG TRĂM TUỔI TRÈO QUA CỬA SỔ VÀ BIẾN MẤT', '13699-ong-tram-tuoi-treo-qua-cua-so-va-bien-mat-1.jpg', 'Jonas Jonasson', 'Tuổi Trẻ', '305', 'Mới 100%, Còn hàng', '', '0', '<p>V&agrave;o ng&agrave;y sinh nhật lần thứ 100 của m&igrave;nh, cụ &ocirc;ng Allan Karlsson đột nhi&ecirc;n tr&egrave;o qua của sổ ng&ocirc;i nh&agrave; dưỡng l&atilde;o - Nh&agrave; Gi&agrave; - v&agrave; biến mất. Ở c&aacute;i tuổi 100 hiếm ai đạt tới th&igrave; cụ c&oacute; thể đi đ&acirc;u được? Một cuộc truy t&igrave;m tr&ecirc;n khắp nước Thụy Điển diễn ra từ ph&iacute;a những người c&oacute; tr&aacute;ch nhiệm chăm nom cụ cũng như ch&iacute;nh quyền sở tại. Song song với cuộc truy t&igrave;m nh&acirc;n đạo ấy, một cuộc truy t&igrave;m đuổi bắt kh&aacute;c gay cấn hơn, xảy đến từ một t&ecirc;n tội phạm, kẻ đ&atilde; ngớ ngẩn hoặc đ&uacute;ng hơn, bất cẩn trao vali 50 triệu crown v&agrave;o tay cụ gi&agrave; n&agrave;y.</p>\r\n\r\n<p>Nhưng một người đ&atilde; sống qua một thế kỷ th&igrave; kh&ocirc;ng dễ g&igrave; t&oacute;m cụ ta được. Cuốn tiểu thuyết hồi tưởng lại cuộc đời phi&ecirc;u lưu của Allan Karlsson, người đ&atilde; đi khắp thế giới từ những năm trước Đại chiến thế giới thứ nhất đến cuộc Thế chiến thứ hai, từ nước Nga X&ocirc; Viết tới nước Mỹ si&ecirc;u cường v&agrave; nước Trung Quốc con rồng đang l&ecirc;n ở Viễn Đ&ocirc;ng. Cuốn tiểu thuyết với giọng điệu h&oacute;m hỉnh tr&agrave;o lộng, dẫn dắt người đọc chu du c&ugrave;ng Allan qua những t&igrave;nh huống giả tưởng l&agrave;m bật l&ecirc;n c&aacute;i nh&igrave;n tưng tửng về thế giới n&agrave;y. Những xung đột văn h&oacute;a, &yacute; thức hệ v&agrave; những n&eacute;t kh&aacute;c thường của c&aacute;c v&ugrave;ng đất xa x&ocirc;i, c&agrave;ng chứng tỏ sự đa dạng của nh&acirc;n loại trong thế giới c&oacute; vẻ phẳng n&agrave;y.</p>\r\n\r\n<p>Cuốn tiểu thuyết &Ocirc;ng trăm tuổi tr&egrave;o qua cửa sổ v&agrave; biến mất đ&atilde; trở th&agrave;nh hiện tượng quốc gia ở Thụy Điển, đem lại cho người đọc một c&aacute;i nh&igrave;n h&agrave;i hước k&iacute;n đ&aacute;o của văn h&oacute;a Bắc &Acirc;u, nơi c&oacute; truyền thống t&ocirc;n qu&yacute; văn học l&acirc;u đời.</p>\r\n\r\n<p>Mời c&aacute;c bạn đ&oacute;n đọc &Ocirc;ng trăm tuổi tr&egrave;o qua cửa sổ v&agrave; biến mất của t&aacute;c giả Jonas Jonasson.</p>\r\n'),
(88, 1, 'YÊU NHỮNG ĐIỀU KHÔNG HOÀN HẢO', '13654-yeu-nhung-dieu-khong-hoan-hao-1.jpg', 'Hae Min', 'Lao Động', '191', 'Mới 100%, Còn hàng', '', '0', '<p>T&igrave;nh y&ecirc;u thương vượt qua mọi hiểu biết của con người</p>\r\n\r\n<p>Đ&ocirc;i khi ch&uacute;ng ta gặp được những bộ phim để lại dư &acirc;m trong ta suốt một thời gian d&agrave;i. Đối với t&ocirc;i, A river runs through it l&agrave; một bộ phim như thế. Lấy bối cảnh thi&ecirc;n nhi&ecirc;n đẹp đẽ của tiểu bang Montana nước Mỹ v&agrave;o đầu thế kỷ 20, bộ phim kể về gia đ&igrave;nh Maclean, một gia đ&igrave;nh t&ocirc;n s&ugrave;ng sở th&iacute;ch c&acirc;u c&aacute; như một t&ocirc;n gi&aacute;o. Người cha của gia đ&igrave;nh ấy l&agrave; một mục sư c&oacute; hai cậu con trai, cậu con trai cả Norman thuận theo &yacute; cha trở th&agrave;nh giảng vi&ecirc;n, c&ograve;n cậu con trai thứ Paul lại l&agrave;m ph&oacute;ng vi&ecirc;n cho một tờ b&aacute;o địa phương, sống cuộc sống c&oacute; phần nổi loạn. Thế rồi Paul kh&ocirc;ng trả nổi những khoản nợ ph&aacute;t sinh do đ&aacute;nh bạc, anh bị đ&aacute;nh đến chết tr&ecirc;n đường phố. Người cha roi v&agrave;o nỗi đau tột c&ugrave;ng v&igrave; mất con, v&agrave;o buổi th&aacute;nh lễ ng&agrave;y Ch&uacute;a nhật, &ocirc;ng cầu nguyện cho con m&igrave;nh v&agrave; n&oacute;i với c&aacute;c t&iacute;n đồ một c&acirc;u đầy cảm x&uacute;c.</p>\r\n\r\n<p>&quot;Ch&uacute;ng ta vẫn c&oacute; thể y&ecirc;u thương nhau trọn vẹn m&agrave; kh&ocirc;ng cần hiểu nhau một c&aacute;ch vẹn to&agrave;n.&quot;</p>\r\n'),
(89, 1, 'PAPILLON - NGƯỜI TÙ KHỔ SAI', '13635-papillon-nguoi-tu-kho-sai-1.jpg', ' Henri Charrière', 'Thanh Niên', '236', 'Mới 100%, Còn hàng', '', '0', '<p>.. Bị xử &aacute;n đ&agrave;y khổ sai chung th&acirc;n v&igrave; tội giết người căn cứ v&agrave;o lời khai của một nh&acirc;n chứng đ&atilde; được c&ocirc;ng an gia c&ocirc;ng trước, Charriere quyết ch&iacute; chuẩn bị vượt ngục ngay từ đầu. Anh quyết sống v&agrave; tho&aacute;t ra khỏi trại khổ sai để trả th&ugrave;. Quyết t&acirc;m ấy đ&atilde; l&agrave;m cho anh c&oacute; đủ sức mạnh chịu đựng mọi thử th&aacute;ch. Kh&ocirc;ng c&oacute; một mối nguy hiểm n&agrave;o l&agrave;m cho anh l&ugrave;i bước, kh&ocirc;ng c&oacute; một phen thất bại &ecirc; chề n&agrave;o l&agrave;m cho anh nhụt ch&iacute;. Nếu kể cả những mưu toan vượt ngục đ&atilde; bị vỡ lở ngay từ khi đang chuẩn bị, Charriere, biệt hiệu Bươm Bướm, đ&atilde; tổ chức cả thảy ch&iacute;n lần vượt ngục trước khi th&agrave;nh c&ocirc;ng v&agrave; được nhận cư tr&uacute; ở Venezuela như một c&ocirc;ng d&acirc;n ch&iacute;nh thức&hellip;</p>\r\n\r\n<p>Năm 1967, hơn ba mươi lăm năm sau khi anh bị bắt v&agrave; hơn hai mươi năm sau khi anh trở th&agrave;nh c&ocirc;ng d&acirc;n tự do của nước Venezuela, Henri Charriere, l&uacute;c bấy giờ đang t&uacute;ng thiếu v&igrave; mới bị ph&aacute; sản, nh&acirc;n đọc một cuốn hồi k&yacute; phi&ecirc;u lưu đ&atilde; đem lại cho nữ t&aacute;c giả h&agrave;ng triệu đồng, nảy ra &yacute; viết lại những cuộc vượt ngục của bản th&acirc;n. Anh nhờ bạn b&egrave; đ&aacute;nh m&aacute;y theo lời anh kể rồi đem mười ba tập đ&aacute;nh m&aacute;y ấy gửi cho nh&agrave; văn J. P. Castelnau nhờ &ocirc;ng giao cho một người n&agrave;o biết viết văn viết lại cho th&agrave;nh một cuốn s&aacute;ch c&oacute; thể xuất bản được. Castelnau đ&atilde; kh&ocirc;ng l&agrave;m theo &yacute; Charriere: &ocirc;ng cho in ngay ch&iacute;nh bản thảo của Charriere, sau khi chữa lại một v&agrave;i lỗi ngữ ph&aacute;p v&agrave; lỗi ch&iacute;nh tả. Nh&agrave; văn biết r&otilde; rằng kh&ocirc;ng c&oacute; một nh&agrave; văn chuy&ecirc;n nghiệp n&agrave;o c&oacute; thể c&oacute; được c&aacute;ch kể chuyện sinh động v&agrave; đầy cảm x&uacute;c ch&acirc;n thật, hồn nhi&ecirc;n của ch&iacute;nh người đ&atilde; sống qua những sự việc được kể. Sức hấp dẫn kỳ lạ của thi&ecirc;n tự sự sở dĩ c&oacute; được ch&iacute;nh l&agrave; v&igrave; người kể dường như sống lại một lần nữa những sự việc thật đ&atilde; xảy ra với đầy đủ những cảm x&uacute;c s&acirc;u đậm v&agrave; m&atilde;nh liệt của m&igrave;nh l&uacute;c bấy giờ. T&acirc;m hồn nhiệt th&agrave;nh của Charriere đ&atilde; cho ph&eacute;p anh thực hiện được điều đ&oacute;.</p>\r\n'),
(90, 2, ' LÀM GIÀU KHÔNG CẦN MAY MẮN', '13872-lam-giau-khong-can-may-man-1.jpg', 'Naval Ravikant', 'Tuổi Trẻ', '116', 'Mới 100%, Còn hàng', '', '1', '<p>Naval: Tiền nong l&agrave; một vấn đề chung của mọi người. Ai cũng muốn m&igrave;nh gi&agrave;u c&oacute;, nhưng kh&ocirc;ng phải ai cũng t&igrave;m được những nguy&ecirc;n tắc đ&uacute;ng đắn để l&agrave;m theo hay một bộ khung vững chắc để dựa v&agrave;o. Đ&acirc;y cũng l&agrave; l&yacute; do m&agrave; t&ocirc;i thực hiện buổi n&oacute;i chuyện n&agrave;y.</p>\r\n\r\n<p>T&agrave;i sản n&ecirc;n l&agrave; thứ m&agrave; bạn muốn theo đuổi. L&yacute; do l&agrave; v&igrave; t&agrave;i sản l&agrave; thứ c&oacute; thể sinh ra lợi nhuận cho bạn ngay cả khi bạn ngủ. T&agrave;i sản c&oacute; thể l&agrave; một nh&agrave; m&aacute;y bạn sở hữu, d&agrave;n tr&acirc;u đ&agrave;o bitcoin, robot sản xuất h&agrave;ng h&oacute;a 24/7. Một phần mềm chạy 24/24 phục vụ kh&aacute;ch h&agrave;ng cũng c&oacute; thể coi l&agrave; một loại t&agrave;i sản. Nếu hiểu theo nghĩa truyền thống hơn th&igrave; t&agrave;i sản l&agrave; tiền trong ng&acirc;n h&agrave;ng đang sinh l&atilde;i cho bạn, hoặc tiền bạn d&ugrave;ng đầu tư cho những hoạt động kinh doanh kh&aacute;c.</p>\r\n\r\n<p>Nh&agrave; đất m&agrave; bạn đang sở hữu cũng c&oacute; thể coi l&agrave; một dạng t&agrave;i sản nếu bạn cho thu&ecirc; n&oacute;, n&oacute; sinh lợi nhuận cho bạn ngay cả khi bạn kh&ocirc;ng d&agrave;nh thời gian cho n&oacute;. Tuy vậy, việc cho thu&ecirc; bất động sản theo t&ocirc;i l&agrave; dạng k&eacute;m nhất trong việc sử dụng đất cho mục đ&iacute;ch thương mại. T&oacute;m lại t&agrave;i sản l&agrave; thứ sinh ra lợi nhuận cho bạn ngay cả khi bạn đang ngủ. Hoặc n&oacute;i đ&uacute;ng hơn l&agrave; thứ gi&uacute;p bạn tạo ra thu nhập thụ động</p>\r\n\r\n<p>Đến đ&acirc;y nhiều bạn sẽ hỏi thế v&igrave; sao m&igrave;nh lại muốn c&oacute; t&agrave;i sản m&agrave; kh&ocirc;ng phải l&agrave; tiền bạc hay địa vị? Sự kh&aacute;c biệt lớn nhất của t&agrave;i sản l&agrave; t&agrave;i sản sẽ mang lại cho bạn sự tự do. Tự do ấy l&agrave; việc bạn được giải tho&aacute;t khỏi việc phải thức dậy l&uacute;c 7 giờ s&aacute;ng rồi lật đật đến c&ocirc;ng ty, l&agrave; việc bạn sẽ kh&ocirc;ng chỉ d&agrave;nh phần lớn thời gian nhiều năng lượng v&agrave; t&iacute;ch cực trong cuộc sống chỉ để l&agrave;m những việc bạn kh&ocirc;ng cảm thấy h&agrave;i l&ograve;ng chỉ v&igrave; thứ gọi l&agrave; lương th&aacute;ng.</p>\r\n\r\n<p>C&oacute; t&agrave;i sản l&agrave; bạn c&oacute; th&ecirc;m sự tự do. Ngo&agrave;i tự do t&agrave;i ch&iacute;nh như kiểu mua nh&agrave;, mua xe, mua điện thoại, n&oacute; c&ograve;n cho ta sự tự do về c&aacute; nh&acirc;n. Tự do c&aacute; nh&acirc;n l&agrave; việc bạn c&oacute; thể thật sự sống theo c&aacute;ch m&agrave; m&igrave;nh muốn. Đấy mới ch&iacute;nh l&agrave; thứ tự do tối thượng m&agrave; bạn n&ecirc;n t&igrave;m kiếm.</p>\r\n\r\n<p>Nhưng tự do l&agrave; thứ ai cũng muốn, hay n&oacute;i đ&uacute;ng hơn l&agrave; cả thế giới n&agrave;y ai cũng mong cầu tự do. Bạn phải thật sự muốn n&oacute;, phải t&igrave;m kiếm n&oacute; v&agrave; phải bỏ sức ra để đạt được n&oacute;. Nhưng đừng lo, t&ocirc;i sẽ n&oacute;i với bạn một tin vui rằng tr&ograve; chơi t&agrave;i sản n&agrave;y l&agrave; một tr&ograve; chơi c&oacute; tổng dương, nghĩa l&agrave; cơ hội sẽ d&agrave;nh cho rất nhiều người. Thế nhưng tr&ograve; chơi n&agrave;y cũng c&oacute; t&iacute;nh cạnh tranh khốc liệt, nguồn lực trong x&atilde; hội hiện đại l&agrave; dồi d&agrave;o nhưng vẫn c&oacute; hạn, do đ&oacute; nếu bạn muốn tận dụng được những nguồn lực ấy cho mục ti&ecirc;u ph&aacute;t triển t&agrave;i sản cho ch&iacute;nh bạn, bạn phải thật sự nỗ lực để nổi bật, thế th&igrave; x&atilde; hội mới c&oacute; thể chuyển giao những nguồn lực ấy cho bạn.</p>\r\n'),
(91, 2, 'DẠY TRẺ BIẾT ĐỌC SỚM', '13850-day-tre-biet-doc-som-1.jpg', 'Glenn Doman', 'Thanh Niên', '95', 'Mới 100%, Còn hàng', '', '1', '<p>Được xuất bản lần đầu ti&ecirc;n v&agrave;o năm 1964, cuốn s&aacute;ch n&agrave;y thực sự l&agrave; mốc khởi đầu cho cuộc C&aacute;ch mạng Mềm. Những &ocirc;ng bố b&agrave; mẹ ti&ecirc;n phong m&ecirc; mải với cuốn s&aacute;ch. Họ l&agrave; những người đầu ti&ecirc;n nhận ra đ&acirc;y l&agrave; một cuộc phi&ecirc;u lưu kh&aacute;m ph&aacute; thế giới k&igrave; diệu v&agrave; m&ecirc;nh m&ocirc;ng của sự ph&aacute;t triển n&oacute;i chung v&agrave; tr&iacute; n&atilde;o n&oacute;i ri&ecirc;ng của con trẻ. Những &ocirc;ng bố b&agrave; mẹ n&agrave;y biết rằng trẻ em th&ocirc;ng minh hơn mọi người nghĩ nhiều. Vậy l&agrave; họ bắt đầu t&igrave;m hiểu v&agrave; họ đ&atilde; l&agrave;m được một việc tuyệt vời.</p>\r\n\r\n<p>Cuốn s&aacute;ch n&agrave;y ng&agrave;y c&agrave;ng được xuất bản rộng r&atilde;i tr&ecirc;n thế giới kể từ lần đầu ti&ecirc;n đạt 5 triệu bản tr&ecirc;n 22 nước. Tất cả những điều được nhắc đến trong cuốn s&aacute;ch đều đ&atilde; trở th&agrave;nh hiện thực d&ugrave; đ&atilde; hơn 40 năm tr&ocirc;i qua.</p>\r\n'),
(92, 2, 'LUYỆN TRÍ NHỚ - CÁI THIỆN TRÍ NHỚ CHỈ TRONG 7 NGÀY', '13835-luyen-tri-nho-cai-thien-tri-nho-chi-trong-7-ngay-1.jpg', 'Alpha Book', 'Alpha Book', '97', 'Mới 100%, Còn hàng', '', '1', '<p>Đ&atilde; bao nhi&ecirc;u lần bạn &ldquo;QU&Ecirc;N&rdquo; những thứ l&atilde;ng xẹt nhưng những hệ quả k&eacute;o theo th&igrave; lại v&ocirc; c&ugrave;ng to lớn? Bạn nhiều lần than tr&aacute;ch bản th&acirc;n rằng: &ldquo;Sao c&ograve;n trẻ m&agrave; đầu &oacute;c đ&atilde;ng tr&iacute; thế kh&ocirc;ng biết?&rdquo; Hay đ&atilde; bao nhi&ecirc;u lần bạn bị bạn b&egrave; đồng nghiệp gọi l&agrave; &ldquo;đ&atilde;ng tr&iacute;&rdquo;; &ldquo;đầu &oacute;c tr&ecirc;n m&acirc;y tr&ecirc;n gi&oacute;&rdquo;. Nếu bạn kh&ocirc;ng thể nhớ hết nổi số lần bạn &ldquo;QU&Ecirc;N&rdquo; th&igrave; việc bạn cần l&agrave;m ngay b&acirc;y giờ l&agrave; học c&aacute;ch để &ldquo;NHỚ&rdquo; th&ocirc;ng qua cuốn s&aacute;ch Alpha Books sắp giới thiệu sau đ&acirc;y Luyện tr&iacute; nhớ.</p>\r\n\r\n<p>Kh&ocirc;ng thể phủ nhận &ldquo;mặt mạnh&rdquo; duy nhất của việc qu&ecirc;n đ&oacute; l&agrave; bạn c&oacute; thể đổ lỗi mọi việc cho n&oacute;, bạn l&agrave;m sai một điều g&igrave; đ&oacute; v&agrave; chỉ đơn giản n&oacute;i &ldquo;em xin lỗi, em qu&ecirc;n mất&rdquo;. Tuy nhi&ecirc;n, ngo&agrave;i mặt mạnh duy nhất đ&oacute; ra, chắc hẳn việc qu&ecirc;n đ&atilde; kh&ocirc;ng &iacute;t lần l&agrave;m bạn phải khốn đốn, qu&ecirc;n l&agrave;m b&agrave;i tập về nh&agrave;, qu&ecirc;n mất để m&oacute;n đồ n&agrave;y ở đ&acirc;u, qu&ecirc;n kh&ocirc;ng l&agrave;m việc g&igrave;, qu&ecirc;n, qu&ecirc;n &hellip; C&oacute; rất nhiều thứ trong cuộc sống h&agrave;ng ng&agrave;y m&agrave; bạn buộc phải nhớ khiến cho đầu &oacute;c của ch&uacute;ng ta bị qu&aacute; tải. Hệ quả của việc đ&oacute; l&agrave; ch&uacute;ng ta trong ph&uacute;t chốc bỗng kh&ocirc;ng thể nhớ hết nổi những việc ch&uacute;ng ta cần l&agrave;m, những thứ ch&uacute;ng ta cần ghi nhớ.</p>\r\n\r\n<p>Chỉ cần 10 ph&uacute;t mỗi ng&agrave;y h&atilde;y luyện tập ghi nhớ những việc sau:</p>\r\n\r\n<p>Ghi nhớ ng&ocirc;n ngữ.</p>\r\n\r\n<p>Ghi nhớ nội dung b&agrave;i học.</p>\r\n\r\n<p>Ghi nhớ lịch tr&igrave;nh v&agrave; đường đi.</p>\r\n\r\n<p>V&agrave; c&ograve;n nhiều nhiều điều nữa, bạn c&oacute; thể học c&aacute;ch ghi nhớ trong cuốn Luyện tr&iacute; nhớ. Nếu bạn thấy nhiều đ&acirc;y vẫn chưa đủ cho bạn để bạn c&oacute; thể bỏ th&oacute;i &ldquo;QU&Ecirc;N&rdquo; th&igrave; Alpha xin m&aacute;ch nhỏ với bạn một c&aacute;ch cũng v&ocirc; c&ugrave;ng đơn giản, đ&oacute; l&agrave; h&atilde;y d&agrave;nh ra 5 ph&uacute;t trước khi ngủ để nhớ lại to&agrave;n bộ mọi việc một c&aacute;ch chi tiết trong suốt cả một ng&agrave;y của bạn.</p>\r\n'),
(93, 2, 'LỜI VÀNG CỦA BỐ', '13735-loi-vang-cua-bo-1.jpg', 'Justin Halpern', 'Lao Động', '117', 'Mới 100%, Còn hàng', '', '1', '<p>&ldquo;Lời v&agrave;ng của bố&rdquo; của t&aacute;c giả Justin Samuel Halpern như một lời chia sẻ về cuộc sống gia đ&igrave;nh người Mỹ trung lưu, kh&ocirc;ng phải cuộc sống trong phim ảnh m&agrave; l&agrave; cuộc sống thật với v&ocirc; v&agrave;n kh&oacute; khăn. Đ&oacute; l&agrave; c&aacute;c nh&agrave; bi&ecirc;n kịch tương lai l&agrave;m bồi b&agrave;n trong nh&agrave; h&agrave;ng, l&agrave; chuy&ecirc;n gia trong lĩnh vực &ldquo;dược phẩm hạt nh&acirc;n&rdquo; l&agrave;m việc cật lực h&agrave;ng ng&agrave;y tới tận tối khuya với rất nhiều &aacute;p lực.</p>\r\n\r\n<p>Justin Samuel Halpern đặc biệt quan t&acirc;m đến mối quan hệ của anh với cha m&igrave;nh, một người sống thẳng thắn đến dường như th&ocirc; lỗ. Những lời n&oacute;i kh&ocirc;ng k&eacute;m phần dung tục của &ocirc;ng lại chứa chan t&igrave;nh y&ecirc;u thương v&agrave; mang những b&agrave;i học đầy &yacute; nghĩa về cuộc sống.</p>\r\n\r\n<p>Đ&oacute; l&agrave; những th&ocirc;ng điệp về nh&atilde;n quan trung thực khi suy x&eacute;t mọi vấn đề trong cuộc sống đồng thời l&agrave; lời khuy&ecirc;n ch&uacute;ng ta phải l&agrave;m việc chăm chỉ, nỗ lực kh&ocirc;ng ngừng, lắng nghe v&agrave; suy nghĩ, tận t&acirc;m, quan s&aacute;t cẩn thận mọi thứ xung quanh cũng như việc đối xử tốt với những người xứng đ&aacute;ng được như thế.</p>\r\n\r\n<p>&ldquo;Lời v&agrave;ng của bố&rdquo; mới đầu được Justin Samuel Halpern đưa l&ecirc;n trang Twitter của ri&ecirc;ng m&igrave;nh, chỉ trong một thời gian ngắn đ&atilde; c&oacute; h&agrave;ng trăm ng&agrave;n người theo d&otilde;i. Khi được xuất bản (th&aacute;ng 5/2010), ngay lập tức &ldquo;Lời v&agrave;ng của bố&rdquo; đ&atilde; b&aacute;n được một triệu bản trong v&ograve;ng một năm.</p>\r\n\r\n<p>Cuốn s&aacute;ch đ&atilde; g&acirc;y tiếng vang tr&ecirc;n văn đ&agrave;n Mỹ, thậm ch&iacute; đ&atilde; được dựng th&agrave;nh một series truyền h&igrave;nh chiếu tr&ecirc;n k&ecirc;nh CBS do h&atilde;ng truyền h&igrave;nh Warner Bros sản xuất. S&aacute;ch c&ograve;n b&aacute;n được bản quyền tới 21 quốc gia tr&ecirc;n thế giới, trong đ&oacute; c&oacute; Việt Nam.</p>\r\n'),
(94, 2, ' YES OR NO NHỮNG QUYẾT ĐỊNH THAY ĐỔI CUỘC SỐNG', '13734-yes-or-no-nhung-quyet-dinh-thay-doi-cuoc-song-1.jpg', ' Spencer Johnson', 'Thanh Niên', '95', 'Mới 100%, Còn hàng', '', '0', '<p>C&acirc;u chuyện kể về h&agrave;nh tr&igrave;nh của một ch&agrave;ng trai đi t&igrave;m b&iacute; quyết của th&agrave;nh c&ocirc;ng v&agrave; hạnh ph&uacute;c từ những quyết định của m&igrave;nh. Ch&agrave;ng trai tham gia v&agrave;o chuyến th&aacute;m hiểm để trải nghiệm v&agrave; học hỏi c&aacute;ch giải quyết c&aacute;c vấn đề một c&aacute;ch hiệu quả v&agrave; đ&uacute;ng đắn nhất, những kinh nghiệm c&oacute; thể &aacute;p dụng cho bất kỳ ai thấy kh&oacute; khăn v&agrave; bế tắc trong c&ocirc;ng việc v&agrave; cuộc sống của m&igrave;nh.</p>\r\n'),
(95, 3, ' CHỦ NGHĨA VÔ THẦN', '13652-chu-nghia-vo-than-1.jpg', ' Julian Baggini', 'Lao Động', '150', 'Mới 100%, Còn hàng', '', '1', '<p>&ldquo;Chủ nghĩa v&ocirc; thần thường được coi l&agrave; một niềm tin ti&ecirc;u cực, đen tối v&agrave; bi quan, được đặc trưng bởi sự b&aacute;c bỏ c&aacute;c gi&aacute; trị v&agrave; mục đ&iacute;ch v&agrave; sự phản đối quyết liệt đối với t&ocirc;n gi&aacute;o. Chủ nghĩa v&ocirc; thần: Một giới thiệu rất ngắn được đặt ra để xua tan những huyền thoại xung quanh chủ nghĩa v&ocirc; thần v&agrave; cho thấy một cuộc sống kh&ocirc;ng c&oacute; niềm tin t&ocirc;n gi&aacute;o c&oacute; thể t&iacute;ch cực, c&oacute; &yacute; nghĩa v&agrave; đạo đức như thế n&agrave;o. N&oacute; cũng đối mặt với sự thất bại của c&aacute;c quốc gia v&ocirc; thần ch&iacute;nh thức trong Thế kỉ XX. Cuốn s&aacute;ch tr&igrave;nh b&agrave;y một trường hợp tr&iacute; tuệ cho chủ nghĩa v&ocirc; thần, dựa nhiều v&agrave;o những l&iacute; lẽ t&iacute;ch cực cho sự thật của n&oacute; cũng như về những lập luận ti&ecirc;u cực chống lại t&ocirc;n gi&aacute;o&rdquo;.</p>\r\n'),
(96, 3, ' VÀI NÉT SƠ LƯỢC VỀ SỰ PHÁT TRIỂN CỦA TRIẾT HỌC TRUNG HOA', '13131-vai-net-so-luoc-ve-su-phat-trien-cua-triet-hoc-trung-hoa-1.jpg', ' Nguyễn Hiến Lê', 'Tuổi Trẻ', '121', 'Mới 100%, Còn hàng', '', '1', '<p>Trong bộ Hồi k&iacute;, cụ Nguyễn Hiến L&ecirc; cho biết đ&ocirc;i điều về bộ Đại cương triết học Trung Quốc (ĐCTHTQ) như sau:</p>\r\n\r\n<p>&ldquo;Do t&ocirc;i đề nghị v&agrave; ph&acirc;n c&ocirc;ng: &ocirc;ng Giản Chi l&atilde;nh phần Vũ trụ luận (II) v&agrave; Tri thức luận (III), t&ocirc;i vốn th&iacute;ch c&aacute;i g&igrave; cụ thể, thực tiễn, l&atilde;nh phần Nh&acirc;n sinh luận (IV), Ch&iacute;nh trị luận (IV). V&igrave; c&ocirc;ng việc của t&ocirc;i dễ hơn của &ocirc;ng Giản Chi, n&ecirc;n t&ocirc;i l&atilde;nh th&ecirc;m phần I: V&agrave;i n&eacute;t sơ lược về sự ph&aacute;t triển của triết học Trung Hoa, v&agrave; phần VI: Tiểu sử c&aacute;c triết gia; hai phần sau n&agrave;y đều ngắn. To&agrave;n bộ gồm hai cuốn: Thượng tr&ecirc;n 800 trang v&agrave; Hạ gần 900 trang. In cả chữ H&aacute;n, để ri&ecirc;ng ở cuối mỗi cuốn.</p>\r\n\r\n<p>&ldquo;C&ocirc;ng việc n&agrave;y rất mệt. T&ocirc;i th&uacute; thực nếu kh&ocirc;ng c&oacute; &ocirc;ng Giản Chi th&igrave; t&ocirc;i kh&ocirc;ng d&aacute;m mạo hiểm v&agrave;o. Ch&uacute;ng t&ocirc;i tra cứu tất cả c&aacute;c s&aacute;ch Trung triết bằng Hoa ngữ, Việt ngữ v&agrave; Ph&aacute;p ngữ m&agrave; ch&uacute;ng t&ocirc;i kiếm được, nhất l&agrave; bộ Trung Quốc triết học đại cương của Vũ Đồng m&agrave; &ocirc;ng bạn Tạ Trọng Hiệp ở Paris kiếm cho được.</p>\r\n\r\n<p>&ldquo;Sự hợp t&aacute;c với &ocirc;ng Giản Chi rất th&uacute; vị, ch&uacute;ng t&ocirc;i l&agrave;m việc đều si&ecirc;ng năng, cẩn thận, biết dung ho&agrave; &yacute; kiến với nhau, học th&ecirc;m được của nhau.</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng t&ocirc;i theo Vũ Đồng, tr&igrave;nh b&agrave;y triết học Trung Hoa theo chiều ngang chứ kh&ocirc;ng theo chiều dọc, nghĩa l&agrave; chia th&agrave;nh từng vấn đề chứ kh&ocirc;ng từng thời đại, từng m&ocirc;n ph&aacute;i. C&aacute;ch tr&igrave;nh b&agrave;y đ&oacute; rất mới mẻ ở nước ta (m&agrave; cũng &iacute;t thấy ở Trung Hoa). Nhưng ở đầu bộ ch&uacute;ng t&ocirc;i cũng th&ecirc;m một phần t&oacute;m tắt lịch sử triết học Trung Hoa, phần n&agrave;y d&agrave;i tr&ecirc;n 100 trang (c&oacute; thể in ri&ecirc;ng v&agrave;o loại s&aacute;ch phổ th&ocirc;ng như loại Que sais je? ở Ph&aacute;p), v&agrave; ở cuối bộ lại th&ecirc;m một phần nữa về tiểu sử mỗi triết gia, như vậy sửa được c&aacute;i khuyết điểm của lối tr&igrave;nh b&agrave;y theo chiều ngang, m&agrave; giữ được c&aacute;i ưu điểm l&agrave; dễ tra cứu khi muốn t&igrave;m hiểu về một vấn đề n&agrave;o.</p>\r\n'),
(97, 3, 'THƯỢNG ĐẾ, THIÊN NHIÊN, NGƯỜI, TÔI VÀ TA', '12513-thuong-de-thien-nhien-nguoi-toi-va-ta-1.jpg', ' Cao Huy Thuần', 'Lao Động', '126', 'Mới 100%, Còn hàng', '', '0', '<p>Quyển s&aacute;ch n&agrave;y tập hợp 6 b&agrave;i giảng m&agrave; t&aacute;c giả đ&atilde; ho&agrave;n th&agrave;nh v&agrave;o th&aacute;ng 7/1999 tại Học viện Phật gi&aacute;o Huế dưới đề t&agrave;i: &quot;Triết l&yacute; luật v&agrave; tư tưởng Phật gi&aacute;o&quot;</p>\r\n\r\n<p>Lần đầu ti&ecirc;n tại Việt Nam, triết l&yacute; luật T&acirc;y phương được tr&igrave;nh b&agrave;y ở bậc đại học. Cũng l&agrave; lần đầu ti&ecirc;n một t&aacute;c giả thử đối chiếu những vấn đề căn bản trong lĩnh vực đ&oacute; với tư tưởng Phật gi&aacute;o.</p>\r\n\r\n<p>Cao Huy Thuần l&agrave; gi&aacute;o sư Đại học Amiens (Ph&aacute;p) v&agrave; gi&aacute;m đốc Trung t&acirc;m nghi&ecirc;n cứu sự h&igrave;nh th&agrave;nh &Acirc;u Ch&acirc;u tại đại học đ&oacute;</p>\r\n'),
(98, 3, 'CÁC HỌC THUYẾT VỀ NHÂN CÁCH', '8492-cac-hoc-thuyet-ve-nhan-cach-1 (1).jpg', 'Barry Smith', 'Văn Hóa', '808', 'Mới 100%, Còn hàng', '', '0', '<p>C&oacute; lẽ, khoa học nghi&ecirc;n cứu về nh&acirc;n c&aacute;ch v&agrave; h&agrave;nh vi của con người l&agrave; nghi&ecirc;n cứu th&uacute; vị nhất được thực hiện từ trước đến nay. Tại sao con người lại cư xử như thế? Tại sao bạn lại buồn ch&aacute;n, giận dữ, vui sướng hoặc lo &acirc;u? Những chuẩn mực về h&agrave;nh vi được thiết lập như thế n&agrave;o v&agrave; ch&uacute;ng thay đổi ra sao? Những kiểu h&agrave;nh vi kỳ qu&aacute;i được con người ph&aacute;t triển như thế n&agrave;o v&agrave; l&agrave;m sao để xử l&yacute; ch&uacute;ng một c&aacute;ch hiệu quả?</p>\r\n\r\n<p>T&acirc;m l&yacute; nh&acirc;n c&aacute;ch l&agrave; một m&ocirc;n khoa học hấp dẫn, sẽ cung cấp c&aacute;c c&acirc;u trả lời cho to&agrave;n bộ những vấn đề tr&ecirc;n, v&agrave; trong đ&oacute; c&oacute; một số vấn đề ch&uacute;ng ta sẽ được đ&aacute;p ứng sớm hơn mong đợi. Kiến thức ch&uacute;ng ta về việc h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển nh&acirc;n c&aacute;ch phần lớn dựa tr&ecirc;n những học thuyết nh&acirc;n c&aacute;ch trọng yếu, cũng như c&aacute;c c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu đ&atilde; được thực hiện nhằm mục đ&iacute;ch kiểm tra v&agrave; mở rộng những học thuyết đ&oacute;. Th&ocirc;ng qua hệ thống học thuyết v&agrave; c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu - kể từ khi những quan điểm của Freud lần đầu ti&ecirc;n được c&ocirc;ng bố - ch&uacute;ng ta sẽ hiểu được nhiều hơn về nh&acirc;n c&aacute;ch v&agrave; h&agrave;nh vi con người.</p>\r\n\r\n<p>Đối với việc nghi&ecirc;n cứu nh&acirc;n c&aacute;ch, ch&uacute;ng t&ocirc;i sẽ cung cấp một tầm nh&igrave;n thấu đ&aacute;o về từng học thuyết trọng yếu, đồng thời so s&aacute;nh cũng như hợp nhất c&aacute;c c&aacute;ch xử l&yacute; kh&aacute;c nhau. Ch&uacute;ng t&ocirc;i chọn những học thuyết c&oacute; ảnh hưởng đ&aacute;ng kể nhất trong lĩnh vực n&agrave;y v&agrave; l&agrave; học thuyết quan trọng nhất hiện nay, đối với hiểu biết của ch&uacute;ng ta về nh&acirc;n c&aacute;ch, để thảo luận. Ch&uacute;ng t&ocirc;i cố gắng viết về mỗi học thuyết với tầm nh&igrave;n vừa cơ bản vừa n&acirc;ng cao. Việc l&yacute; giải kh&ocirc;ng chỉ đơn thuần nhằm tr&igrave;nh b&agrave;y kiến thức về học thuyết nh&acirc;n c&aacute;ch, m&agrave; c&ugrave;ng l&uacute;c ch&uacute;ng t&ocirc;i c&ograve;n cung cấp một tầm nh&igrave;n ch&iacute;nh x&aacute;c v&agrave; to&agrave;n diện về c&aacute;c học thuyết, để hỗ trợ cho việc nghi&ecirc;n cứu s&acirc;u hơn về học thuyết cũng như những ph&acirc;n nh&aacute;nh của n&oacute;.</p>\r\n\r\n<p>Bất kể học thuyết n&agrave;o, nếu tr&igrave;nh b&agrave;y chi tiết m&agrave; kh&ocirc;ng ch&uacute; &yacute; đến những chủ đề c&oacute; li&ecirc;n quan sẽ trở th&agrave;nh những b&agrave;i tập kh&ocirc; cứng, lạnh l&ugrave;ng. V&igrave; thế, mỗi chương của học thuyết sẽ thảo luận về lịch sử bắt nguồn của n&oacute; v&agrave; về th&acirc;n thế sự nghiệp của c&aacute;c nh&agrave; l&yacute; luận trọng yếu, xem x&eacute;t nghi&ecirc;n cứu kết hợp với học thuyết, cung cấp sự đ&aacute;nh gi&aacute; về c&aacute;ch xử l&yacute;, ph&acirc;n t&iacute;ch c&aacute;c &aacute;p dụng li&ecirc;n quan v&agrave; so s&aacute;nh n&oacute; với những học thuyết kh&aacute;c. V&iacute; dụ, ch&uacute;ng ta kh&ocirc;ng chỉ thảo luận thuyết Freud m&agrave; c&ograve;n học c&aacute;ch Freud &aacute;p dụng học thuyết của &ocirc;ng trong việc t&igrave;m ra nguy&ecirc;n nh&acirc;n chứng loạn thần kinh v&agrave; liệu ph&aacute;p ph&acirc;n t&acirc;m học được sử dụng như thế n&agrave;o. Hơn nữa, ch&uacute;ng ta sẽ so s&aacute;nh thuyết Freud với những thuyết kh&aacute;c, cả về sự tương đồng cũng như kh&aacute;c biệt của những &yacute; niệm ch&iacute;nh. Chương cuối c&ugrave;ng sẽ đi xa hơn bằng c&aacute;ch so s&aacute;nh tương quan to&agrave;n diện hơn nữa về những học thuyết kh&aacute;c nhau.</p>\r\n\r\n<p>Tuy mỗi chương học thuyết được sắp xếp tương tự nhưng kh&ocirc;ng giống nhau ho&agrave;n to&agrave;n, v&igrave; ch&uacute;ng t&ocirc;i cố gắng đi theo một tr&igrave;nh tự sắp xếp căn bản khi tr&igrave;nh b&agrave;y mỗi phương ph&aacute;p giải quyết m&agrave; kh&ocirc;ng dựa v&agrave;o một sườn b&agrave;i cứng nhắc. Để gi&uacute;p bạn hiểu, đ&aacute;nh gi&aacute; v&agrave; so s&aacute;nh c&aacute;c học thuyết, trong Chương 1 ch&uacute;ng t&ocirc;i cung cấp một tập hợp c&aacute;c hướng dẫn cần thiết về những &yacute; niệm ch&iacute;nh v&agrave; những định đề xuất hiện trong c&aacute;c c&aacute;ch xử l&yacute; kh&aacute;c nhau.</p>\r\n\r\n<p>Lần xuất bản n&agrave;y, ch&uacute;ng t&ocirc;i cũng đi theo c&aacute;ch bố tr&iacute; cơ bản ở bản gốc nhưng ho&agrave;n thiện hơn khi viết lại theo chiều hướng mở rộng, n&acirc;ng cấp v&agrave; th&ecirc;m v&agrave;o một số điểm mới. Để gi&uacute;p bạn điểm lại những g&igrave; đ&atilde; học, ch&uacute;ng t&ocirc;i hỗ trợ cho bạn những c&acirc;u hỏi d&ugrave;ng để thảo luận. Ch&uacute;ng t&ocirc;i hy vọng rằng ch&uacute;ng sẽ k&iacute;ch th&iacute;ch v&agrave; gi&uacute;p bạn tăng khả năng tư duy.</p>\r\n'),
(99, 3, 'SỬ THI ODYSSEY', '7870-su-thi-odyssey-homer-1.jpg', 'HOMER', 'Tuổi Trẻ', '466', 'Mới 100%, Còn hàng', '', '0', '<p>liad và Odyssey của đại thi hào Homer ch&iacute;nh l&agrave; hai thi&ecirc;n sử thi kinh đi&ecirc;̉n và là c&ocirc;̣t trụ của văn minh phương T&acirc;y. Ngay cả những chính trị gia l&ocirc;̃i lạc như Plato, Aristotle hay bậc qu&acirc;n vương như Alexander Đại Đ&ecirc;́ cũng xem vi&ecirc;̣c đọc hai tác ph&acirc;̉m này là m&ocirc;̣t.</p>\r\n\r\n<p>Bắt đ&acirc;̀u từ những nhà truy&ecirc;̀n giáo cho tới kỷ nguy&ecirc;n toàn c&acirc;̀u hóa và ti&ecirc;́p tục trong tương lai, những ảnh hưởng của văn minh phương T&acirc;y tới Vi&ecirc;̣t Nam kh&ocirc;ng chỉ dừng lại ở khoa học cơ bản, mà còn mở r&ocirc;̣ng đ&ecirc;́n các ngành khoa học xã h&ocirc;̣i và ngh&ecirc;̣ thu&acirc;̣t. Vì v&acirc;̣y, đ&ecirc;̉ thực sự khai thác xu hướng này, chúng ta c&acirc;̀n tìm hi&ecirc;̉u n&ecirc;̀n tảng và c&ocirc;̣i ngu&ocirc;̀n của văn minh phương T&acirc;y: Hy Lạp c&ocirc;̉ đại. Kh&ocirc;ng hề quá lời khi cho rằng, vi&ecirc;̣c tìm hi&ecirc;̉u Hy Lạp c&ocirc;̉ đại là m&ocirc;̣t đi&ecirc;̀u ki&ecirc;̣n ti&ecirc;n quy&ecirc;́t nhằm mang lại sự hi&ecirc;̉u bi&ecirc;́t th&acirc;́u đáo li&ecirc;n quan đ&ecirc;́n những lu&acirc;̣n đi&ecirc;̉m và học thuy&ecirc;́t chủ đạo đang chi ph&ocirc;́i th&ecirc;́ giới mà chúng ta đang s&ocirc;́ng: Lịch Sử và Th&acirc;̀n Thoại, Giới Tính và Xác Th&acirc;n, T&ocirc;n Giáo và H&ocirc;n Nh&acirc;n, Chính Trị và D&acirc;n Chủ&hellip;</p>\r\n\r\n<p>Iliad và Odyssey l&agrave; những bản trường ca của binh lửa v&agrave; gi&aacute;o gươm. Ở đ&oacute; những kẻ nh&uacute;t nh&aacute;t v&agrave; rụt r&egrave; trước chiến trận mới ch&iacute;nh l&agrave; đối tượng bị d&egrave; bỉu. Nhưng Homer kh&ocirc;ng cổ s&uacute;y cho chiến tranh m&agrave; d&ugrave;ng chiến tranh để thử th&aacute;ch con người v&agrave; d&ugrave;ng C&aacute;i Chết để bộc lộ đức hạnh của họ.</p>\r\n\r\n<p>Aristotle diễn giải bi kịch Hy Lạp l&agrave; sự kết hợp của số phận v&agrave; sai lầm c&aacute; nh&acirc;n. Nghĩa l&agrave; một kết cục kh&ocirc;ng vui chỉ th&agrave;nh bi kịch khi phẩm chất tr&iacute; tuệ v&agrave; đức hạnh của nh&acirc;n vật ch&iacute;nh phải vượt hẳn người thường. V&agrave; từ những biến cố đ&oacute; người ta ph&aacute;t lộ ra những g&igrave; đẹp đẽ nhất của m&igrave;nh. Còn Karl Japers cho rằng &yacute; ch&iacute; đương đầu v&agrave; chấp nhận những b&oacute;ng tối v&agrave; sụp đổ, những điều sản sinh từ hiểm nguy v&agrave; nghi&ecirc;m trọng của cuộc đời vượt qu&aacute; sự tri nghiệm của con người, ch&iacute;nh l&agrave; tinh thần g&acirc;y dựng n&ecirc;n văn</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Iliad v&agrave; Odyssey c&ograve;n l&agrave; những suy tư v&agrave; chi&ecirc;m nghiệm của người Hy Lạp cổ đại về mối quan hệ vừa th&acirc;n thuộc vừa xa lạ giữa con người v&agrave; vũ trụ, của linh hồn v&agrave; thế giới, của b&ecirc;n trong v&agrave; b&ecirc;n</p>\r\n\r\n<p>Phương T&acirc;y xem trọng hai bản trường ca n&agrave;y v&igrave; ch&uacute;ng l&agrave; bi&ecirc;n ni&ecirc;n sử của qu&aacute; tr&igrave;nh tiến h&oacute;a của &yacute; thức con người, nhất l&agrave; &yacute; thức về thời gian. Điều đặc biệt của qu&aacute; tr&igrave;nh tiến h&oacute;a &yacute; thức l&agrave; n&oacute; tiếp tục diễn ra ngay cả khi x&aacute;c th&acirc;n đ&atilde; ho&agrave;n tất qu&aacute; tr&igrave;nh biến đổi. Chẳng phải thế m&agrave; ta lu&ocirc;n coi v&oacute;c d&aacute;ng cơ thể của</p>\r\n\r\n<p>Achilles l&agrave; h&igrave;nh mẫu cho nam v&agrave; Helen cho nữ.</p>\r\n\r\n<p>Cuộc chiến th&agrave;nh Troy c&oacute; thể coi như dấu chấm hết cho sự ng&acirc;y thơ c&ograve;n s&oacute;t lại từ thuở hồng hoang. N&oacute; kết th&uacute;c kh&ocirc;ng phải v&igrave; kẻ n&agrave;y mạnh hơn kẻ kia, m&agrave; v&igrave; c&oacute; kẻ n&agrave;y &ldquo;l&aacute;u c&aacute;&rdquo; hơn kẻ kia. Người quyết định vận mệnh cuộc chiến kh&ocirc;ng ai kh&aacute;c hơn l&agrave; Odyssey.</p>\r\n\r\n<p>Nhưng tại sao một kế hoạch rất &ldquo;th&ocirc; sơ&rdquo; như Con ngựa th&agrave;nh Troy lại c&oacute; thể th&agrave;nh c&ocirc;ng? Bối cảnh bấy giờ l&agrave; buổi ho&agrave;ng h&ocirc;n của k&yacute; ức cộng đồng khi mỗi th&agrave;nh vi&ecirc;n c&oacute; thể nh&igrave;n thấy được d&ograve;ng &yacute; thức của người kh&aacute;c, v&agrave; kh&aacute;i niệm lừa lọc, hay ch&iacute;nh x&aacute;c hơn l&agrave; che giấu d&ograve;ng &yacute; thức c&aacute; nh&acirc;n, ho&agrave;n to&agrave;n kh&ocirc;ng tồn tại, cho đến khi Odysseus xuất hiện. Đ&oacute; ch&iacute;nh l&agrave; m&aacute;nh kh&oacute;e đầu ti&ecirc;n trong lịch sử lo&agrave;i người.</p>\r\n\r\n<p>Nhưng điều đặc biệt v&agrave; th&uacute; vị hơn cả l&agrave; sự tương t&aacute;c giữa thần v&agrave; người trong hai thi&ecirc;n sử thi n&agrave;y. C&aacute;c thần Hy Lạp mang h&igrave;nh d&aacute;ng con người, kh&aacute;c với c&aacute;c nền văn minh đương thời kh&aacute;c như Ai Cập hay Mesopotamian. Họ kh&ocirc;ng phải l&agrave; những đấng to&agrave;n năng v&ocirc; bi&ecirc;n, lại c&ograve;n mang đầy đủ những th&oacute;i hư tật xấu như con người. Nhưng điều n&agrave;y lại ch&iacute;nh l&agrave; &yacute; tưởng đột ph&aacute; vĩ đại: Vũ Trụ mang vẻ đẹp v&agrave; huyền b&iacute; của con người. Con người ch&iacute;nh l&agrave; trung t&acirc;m của vũ trụ. Cuộc chiến th&agrave;nh Troy được c&aacute;c vị thần bắt đầu, can thiệp v&agrave; kết th&uacute;c, nhưng tới cuộc đại hải tr&igrave;nh của Odysseus th&igrave; quyền tự quyết định vận mệnh của c&aacute; nh&acirc;n mới l&agrave; yếu tố chủ đạo.</p>\r\n\r\n<p>Trong quá trình đọc, hẳn qu&yacute; đ&ocirc;̣c giả sẽ tìm th&acirc;́y cho mình những trải nghi&ecirc;̣m hữu ích từ những tương đ&ocirc;̀ng và dị bi&ecirc;̣t giữa hi&ecirc;̣n tại và quá khứ.</p>\r\n\r\n<p>Những người Hy Lạp và Trojan kia cũng th&acirc;́y mặt trời lặn và mọc. Họ cũng băn khoăn và suy nghĩ trước quy lu&acirc;̣t b&acirc;́t bi&ecirc;́n của cu&ocirc;̣c s&ocirc;́ng: tu&ocirc;̉i già, b&ecirc;̣nh t&acirc;̣t, tình y&ecirc;u, khát vọng, n&ocirc; dịch, hạnh phúc&hellip; V&agrave; ch&iacute;nh những người Hy Lạp và Trojan kia cũng hi&ecirc;̉u sự hoang tàn và ch&ecirc;́t chóc của chi&ecirc;́n tranh, của n&ocirc; dịch</p>\r\n\r\n<p>Với t&acirc;m th&ecirc;́ đúng mực, chúng ta sẽ thu hoạch được nhi&ecirc;̀u đi&ecirc;̀u hữu ích từ những tác ph&acirc;̉m c&ocirc;̉ đi&ecirc;̉n. Vì suy cho cùng, vi&ecirc;̣c mắc kẹt hoàn toàn trong hi&ecirc;̣n tại hoặc lu&ocirc;n ngoái đ&acirc;̀u hoài vọng v&ecirc;̀ quá khứ chỉ dẫn ta vướng v&agrave;o hết sự v&ocirc; tri này đ&ecirc;́n sự v&ocirc; tri khác.</p>\r\n\r\n<p>Dịch giả Đỗ Kh&aacute;nh Hoan nguy&ecirc;n gi&aacute;o sư v&agrave; trưởng ban Anh văn, Đại học Văn Khoa, Viện Đại học Saigon (1964-1979). Trước kia, khi c&ograve;n ở trong nước, ngo&agrave;i việc giảng dạy văn chương, văn học Anh Mỹ, &ocirc;ng thường chuyển dịch danh t&aacute;c văn chương hai xứ n&agrave;y. Từ ng&agrave;y ra nước ngo&agrave;i, định cư ở Canada, &ocirc;ng d&agrave;nh th&igrave; giờ t&igrave;m hiểu văn nghệ xứ tuyết rừng phong, giới thiệu một số c&acirc;y b&uacute;t s&aacute;ng gi&aacute;, chuyển ngữ nhiều truyện ngắn gi&aacute; trị. Th&ecirc;m v&agrave;o đ&oacute;, qua t&igrave;m hiểu văn học ch&acirc;u Mỹ Latinh, từ 1983 đến nay, &ocirc;ng giới thiệu c&acirc;y b&uacute;t đặc sắc. &Ocirc;ng đ&atilde; xuất bản Văn học Brasil (1997) v&agrave; dự t&iacute;nh xuất bản bộ Văn học Ch&acirc;u Mỹ Latinh.</p>\r\n'),
(100, 4, 'XÂY DỰNG LẠI TRÁI ĐẤT', '13469-xay-dung-lai-trai-dat-1.jpg', 'Mark Everard', 'Tuổi Trẻ', '123', 'Mới 100%, Còn hàng', '', '1', '<p>Việc x&acirc;y dựng lại Tr&aacute;i đất l&agrave;m r&otilde; r&agrave;ng sự phụ thuộc lẫn nhau của lo&agrave;i người với hệ sinh th&aacute;i c&aacute;c h&agrave;nh tinh. C&aacute;c phương ph&aacute;p tiếp cận n&agrave;y dựa tr&ecirc;n việc phục hồi v&agrave; bảo vệ c&aacute;c hệ sinh th&aacute;i nước v&agrave; đất rừng t&agrave;i nguy&ecirc;n biển v&agrave; ven biển thực h&agrave;nh canh t&aacute;c cơ sở hạ tầng đ&ocirc; thị v&agrave; trong chuỗi cung ứng của doanh nghiệp.</p>\r\n'),
(101, 4, 'SINH TRẮC VÂN TAY: KHÁM PHÁ TIỀM NĂNG GIẢI MÃ CUỘC ĐỜI', '13460-sinh-trac-van-tay-kham-pha-tiem-nang-giai-ma-cuoc-doi-1.jpg', 'Richard Unger', 'Lao Động', '153', 'Mới 100%, Còn hàng', '', '1', '<p>Có bao giờ bạn tự hỏi: &ldquo;Tại sao c&oacute; những người th&agrave;nh c&ocirc;ng đến với họ dường như dễ d&agrave;ng hơn những người kh&aacute;c? Yếu tố g&igrave; gi&uacute;p họ vượt l&ecirc;n nhanh v&agrave; sớm như vậy trong khi đa số mọi người vẫn loay hoay ở ph&iacute;a sau?&rdquo; Cuộc sống l&agrave; một chuỗi những lựa chọn v&agrave; hiểu bản th&acirc;n l&agrave; c&aacute;ch để bạn đưa ra những quyết định đ&uacute;ng đắn. Tuy nhi&ecirc;n, bạn sẽ kh&ocirc;ng biết m&igrave;nh ph&ugrave; hợp hay kh&ocirc;ng ph&ugrave; hợp với điều g&igrave; cho đến khi bạn trực tiếp thử nghiệm việc đ&oacute;.</p>\r\n\r\n<p>Vậy nếu bạn hiểu bản th&acirc;n m&igrave;nh ngay từ đầu, biết được những khả năng bẩm sinh của bản th&acirc;n&hellip; hẳn bạn sẽ tiết kiệm được rất nhiều thời gian, c&ocirc;ng sức, tiền bạc cho những trải nghiệm kh&ocirc;ng ph&ugrave; hợp. Và sinh trắc v&acirc;n tay sẽ là c&ocirc;ng cụ ch&iacute;nh x&aacute;c để gi&uacute;p bạn KHÁM PHÁ BẢN TH&Acirc;N tr&ecirc;n cơ sở những nghi&ecirc;n cứu khoa học đ&aacute;ng tin cậy nhất.</p>\r\n\r\n<p>Đặc bi&ecirc;̣t, bạn HOÀN TOÀN c&oacute; thể TỰ SINH TRẮC V&Acirc;N TAY cho bản th&acirc;n bạn v&agrave; cho người th&acirc;n của m&igrave;nh chỉ th&ocirc;ng qua cuốn s&aacute;ch &ldquo;Sinh Trắc V&acirc;n Tay Kh&aacute;m Ph&aacute; Tiềm Năng Giải M&atilde; Cuộc Đời từ dấu v&acirc;n tay của bạn&rdquo; của chuy&ecirc;n gia ph&acirc;n t&iacute;ch tay V&acirc;n Tay Richard Unger.</p>\r\n'),
(102, 4, 'TẾ BÀO GỐC - BÍ MẬT CỦA SUỐI NGUỒN TƯƠI TRẺ', '12227-tazaki-tsukuru-khong-mau-va-nhung-nam-thang-hanh-huong-1.jpg', 'Neil H Riordan', 'Thanh Niên', '457', 'Mới 100%, Còn hàng', '', '1', '<p>&ldquo;Tế b&agrave;o gốc&rdquo; l&agrave; một quyển s&aacute;ch do nh&agrave; khoa học, chuy&ecirc;n gia trong lĩnh vực y học t&aacute;i tạo Tiến sĩ Neil H. Riordan viết. Trong đ&oacute;, &ocirc;ng ghi lại qu&aacute; tr&igrave;nh m&igrave;nh nghi&ecirc;n cứu v&agrave; ph&aacute;t triển th&agrave;nh c&ocirc;ng c&ocirc;ng nghệ Tế b&agrave;o gốc, một liệu ph&aacute;p c&oacute; thể chữa trị Ung thư v&agrave; v&agrave;i những căn bệnh nan y kh&aacute;c.</p>\r\n\r\n<p>Kh&aacute;c hẳn với c&aacute;c phương ph&aacute;p chữa trị ung thư đắt đỏ, l&agrave;m cho người bệnh ng&agrave;y c&agrave;ng suy yếu v&agrave; tỉ lệ th&agrave;nh c&ocirc;ng cũng kh&ocirc;ng qu&aacute; cao, Tế b&agrave;o gốc l&agrave; liệu ph&aacute;p chữa trị với nguy&ecirc;n l&yacute; cơ bản l&agrave; tăng cường hệ miễn dịch để cơ thể tự đ&agrave;o thải c&aacute;c khối u.</p>\r\n\r\n<p>Giả thiết mới về ung thư được đặt ra, Neil cho rằng c&aacute;c khối u ch&iacute;nh l&agrave; một cơ chế tự chữa l&agrave;nh của cơ thể, khi hệ miễn dịch suy yếu, kh&ocirc;ng l&agrave;m tốt chức năng của n&oacute; th&igrave; c&aacute;c khối u sẽ bắt đầu h&igrave;nh th&agrave;nh v&agrave; &ldquo;cố gắng chữa l&agrave;nh những vết thương kh&ocirc;ng thể l&agrave;nh&rdquo;. Ch&iacute;nh c&aacute;ch nh&igrave;n mới n&agrave;y gi&uacute;p &ocirc;ng định hướng cho m&igrave;nh một lối nghi&ecirc;n cứu ho&agrave;n to&agrave;n kh&aacute;c biệt. &Ocirc;ng tập trung n&acirc;ng cao sức đề kh&aacute;ng của cơ thể, dẫn lối n&oacute; chống lại c&aacute;c khối u v&agrave; kết quả l&agrave; ch&uacute;ng dần bị ti&ecirc;u diệt v&agrave; sức khỏe người bệnh ng&agrave;y c&agrave;ng cải thiện.</p>\r\n\r\n<p>&ldquo;Tế b&agrave;o gốc&rdquo; kh&ocirc;ng chỉ l&agrave; nhật k&yacute; qu&aacute; tr&igrave;nh nghi&ecirc;n cứu m&agrave; c&ograve;n l&agrave; niềm hy vọng mới của con người trong việc đẩy l&ugrave;i căn bệnh ung thư. Qua từng trang s&aacute;ch, t&aacute;c giả viết về c&aacute;c trường hợp m&agrave; &ocirc;ng đ&atilde; thử nghiệm vaccine th&agrave;nh c&ocirc;ng, mang lại một cơ hội sống thứ hai cho c&aacute;c bệnh nh&acirc;n, những người mắc phải c&aacute;c căn bệnh m&agrave; ng&agrave;nh c&ocirc;ng nghiệp dược phẩm v&acirc;̃n chưa c&oacute; giải ph&aacute;p hiệu quả.</p>\r\n'),
(103, 4, 'KHOA HỌC VỚI NHỮNG GIẤC MƠ', '10287-khoa-hoc-voi-nhung-giac-mo-1.jpg', 'Lê Giáng', 'Văn Hóa', '316', 'Mới 100%, Còn hàng', '', '0', '<p>Bạn đ&atilde; từng:</p>\r\n\r\n<p>Trong giấc ngủ say sưa, chợt tỉnh giấc bởi một giấc mơ kinh ho&agrave;ng, khiến cho bạn sợ h&atilde;i. Hay chăng l&agrave; một giấc mộng đẹp đẽ giống hệt như một c&acirc;u chuyện cổ t&iacute;ch.</p>\r\n\r\n<p>Liệu rằng những cơn mộng đ&oacute; c&oacute; l&agrave;m cho bạn thắc mắc?</p>\r\n\r\n<p>&#39;&#39;Vạn vật c&oacute; linh nghiệm&#39;&#39; l&agrave; c&acirc;u n&oacute;i của người đời xưa, l&agrave; một quan niệm được kế thừa. Do đ&oacute;, giải mộng đ&atilde; c&oacute; từ l&acirc;u trong x&atilde; hội phương Đ&ocirc;ng cũng như phương T&acirc;y. Lịch sử về c&aacute;c giấc mơ cũng kh&aacute; phong ph&uacute;. Tuy chưa được ph&acirc;n t&iacute;ch hệ thống dưới &aacute;nh s&aacute;ng khoa học nhưng từ l&acirc;u c&aacute;c nh&agrave; t&acirc;m l&yacute;, văn học v&agrave; triết học đ&atilde; đề cập đến vấn đề n&agrave;y. C&aacute;c nh&agrave; nghi&ecirc;n cứu cũng đ&atilde; d&ugrave;ng c&aacute;c loại l&yacute; luận chủ quan hoặc tr&iacute; tưởng tượng để ph&acirc;n t&iacute;ch c&aacute;c giấc mơ v&agrave; đ&aacute;p ứng được phần n&agrave;o nhu cầu tinh thần của con người.</p>\r\n\r\n<p>Những thắc mắc tưởng chừng như kh&ocirc;ng c&oacute; cơ sở nhưng lại được c&aacute;c nh&agrave; khoa học giải đ&aacute;p, gi&uacute;p cho ta phần n&agrave;o c&oacute; thể cảm nhận được th&ocirc;ng điệp qua những giấc mơ của m&igrave;nh.</p>\r\n'),
(104, 4, 'DÒNG THỜI GIAN', '9533-dong-thoi-gian-1.jpg', 'Michael Crichton', 'Lao Động', '679', 'Mới 100%, Còn hàng', '', '0', '<p>Một bức th&ocirc;ng điệp cầu cứu 600 năm tuổi từ vị gi&aacute;o sư sử học đại học Yale gửi cho c&aacute;c sinh vi&ecirc;n của m&igrave;nh ở thời hiện tại. Tr&ecirc;n sa mạc rộng lớn kh&ocirc;ng một b&oacute;ng người, một người đ&agrave;n &ocirc;ng bỗng xuất hiện, kh&ocirc;ng một phương tiện di chuyển. Giữa hai sự kiện k&igrave; lạ kh&aacute;c nhau nửa v&ograve;ng tr&aacute;i đất, c&oacute; một điểm chung kh&oacute; ngờ, đ&oacute; l&agrave; trung t&acirc;m nghi&ecirc;n cứu c&ocirc;ng nghệ lượng tử ITC...</p>\r\n\r\n<p>Với chủ đề đ&atilde; l&agrave;m say đắm khắp c&aacute;c khoa học gia lẫn tiểu thuyết gia viễn tưởng từ hồi c&aacute;c Thuyết Lượng tử v&agrave; Tương đối ra đời, MICHAEL CRICHTON, cha đẻ của C&ocirc;ng vi&ecirc;n kỷ Jura v&agrave; Thế giới nghịch, cưỡi l&ecirc;n D&ograve;ng thời gian để tiến v&agrave;o một cuộc &quot;du h&agrave;nh thời gian&quot;, bắt đầu thử sức với một nhiệm vụ c&ograve;n dang dở: biến &quot;nghịch l&yacute; thời gian&quot; trở n&ecirc;n c&oacute; l&yacute;.</p>\r\n\r\n<p>... Đứng đầu bởi gi&aacute;o sư EDWARD JOHNSTON, nh&oacute;m khảo cổ xuy&ecirc;n thời gian trở về thời Trung cổ. Một tai nạn buộc họ phải ở lại l&acirc;u hơn dự định. Một thời đại h&ugrave;ng tr&aacute;ng v&agrave; t&agrave;n khốc - với những cuộc chiến đẫm m&aacute;u li&ecirc;n mi&ecirc;n, mang tới hai lựa chọn, một l&agrave; chết, hai l&agrave; sống s&oacute;t để trở về hiện tại. Họ phải sống để t&igrave;m ra c&acirc;u trả lời cho những c&acirc;u hỏi cốt tử của ri&ecirc;ng m&igrave;nh đồng thời h&eacute; lộ những b&iacute; mật chết người m&agrave; ITC đang che giấu...</p>\r\n\r\n<p>&nbsp;</p>\r\n');

